const Sidebar = ({ sidebarOpen, setSidebarOpen }: SidebarProps) => {
  const pathname = usePathname();
  const [pageName, setPageName] = useState("dashboard");
  const [isCollapsed, setIsCollapsed] = useLocalStorage("adminSidebarCollapsed", true);
  const { data: session } = useSession();
  
  // Fetch current user's profile to get email
  const { data: currentUser } = useQuery({
    queryKey: ['current-user', session?.user?._id],
    queryFn: () => {
      if (session?.user?._id) {
        return fetchUser(session.user._id);
      }
      return null;
    },
    enabled: !!session?.user?._id,
  });

  // Check if current user is root admin
  const isRootAdmin = currentUser?.email === "admin@thehotelmedia.com";
  
  // Generate menu groups based on root admin status
  const menuGroups = getMenuGroups(isRootAdmin);

  const toggleCollapse = () => {
    const newState = !isCollapsed;
    setIsCollapsed(newState);
    // Dispatch custom event with the new state for same-tab updates
    window.dispatchEvent(
      new CustomEvent("sidebarCollapseChange", {
        detail: { isCollapsed: newState },
      })
    );
  };

  return (
    <ClickOutside onClick={() => setSidebarOpen(false)}>
      <aside
        className={`fixed left-0 top-0 z-9999 flex h-screen flex-col overflow-y-hidden bg-black duration-300 ease-linear dark:bg-boxdark-sidebar lg:translate-x-0 ${
          isCollapsed ? "w-20" : "w-72.5"
        } ${sidebarOpen ? "translate-x-0" : "-translate-x-full"}`}
      >
        {/* <!-- SIDEBAR HEADER --> */}
        <div
          className={`transition-all duration-300 ${
            isCollapsed
              ? "flex flex-col items-center px-0 py-5.5 lg:py-6.5 gap-3"
              : "flex items-center justify-between gap-2 px-6 py-5.5 lg:py-6.5"
          }`}
        >
          {!isCollapsed && (
            <Link href="/dashboard" className="transition-opacity duration-300">
              <Image
                width={100}
                height={100}
                src={"/images/logo/logo.svg"}
                alt="The Hotel Media Logo"
                priority
              />
            </Link>
          )}
          {isCollapsed && (
            <>
              <Link
                href="/dashboard"
                className="flex items-center justify-center transition-opacity duration-300"
              >
                <Image
                  width={56}
                  height={56}
                  src={"/images/logo/logo.svg"}
                  alt="THM Logo"
                  className="h-14 w-14"
                  priority
                />
              </Link>
              <button
                onClick={toggleCollapse}
                aria-label="Expand sidebar"
                className="hidden lg:flex items-center justify-center rounded-md p-1.5 text-bodydark2 hover:bg-boxdark-hover hover:text-white transition-colors duration-200 dark:text-bodydark dark:hover:text-white"
              >
                <svg
                  className="fill-current transition-transform duration-300 rotate-180"
                  width="18"
                  height="18"
                  viewBox="0 0 18 18"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M11.25 13.5L6.75 9L11.25 4.5"
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                </svg>
              </button>
            </>
          )}
          {!isCollapsed && (
            <div className="flex items-center gap-2">
              <button
                onClick={toggleCollapse}
                aria-label="Collapse sidebar"
                className="hidden lg:flex items-center justify-center rounded-md p-1.5 text-bodydark2 hover:bg-boxdark-hover hover:text-white transition-colors duration-200 dark:text-bodydark dark:hover:text-white"
              >
                <svg
                  className="fill-current transition-transform duration-300"
                  width="18"
                  height="18"
                  viewBox="0 0 18 18"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M11.25 13.5L6.75 9L11.25 4.5"
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                </svg>
              </button>
              <button
                onClick={() => setSidebarOpen(!sidebarOpen)}
                aria-controls="sidebar"
                className="block lg:hidden"
              >
                <svg
                  className="fill-current"
                  width="20"
                  height="18"
                  viewBox="0 0 20 18"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M19 8.175H2.98748L9.36248 1.6875C9.69998 1.35 9.69998 0.825 9.36248 0.4875C9.02498 0.15 8.49998 0.15 8.16248 0.4875L0.399976 8.3625C0.0624756 8.7 0.0624756 9.225 0.399976 9.5625L8.16248 17.4375C8.31248 17.5875 8.53748 17.7 8.76248 17.7C8.98748 17.7 9.17498 17.625 9.36248 17.475C9.69998 17.1375 9.69998 16.6125 9.36248 16.275L3.02498 9.8625H19C19.45 9.8625 19.825 9.4875 19.825 9.0375C19.825 8.55 19.45 8.175 19 8.175Z"
                    fill=""
                  />
                </svg>
              </button>
            </div>
          )}
        </div>
        {/* <!-- SIDEBAR HEADER --> */}

        <div className="no-scrollbar flex flex-col overflow-y-auto duration-300 ease-linear">
          {/* <!-- Sidebar Menu --> */}
          <nav
            className={`mt-5 py-4 lg:mt-9 transition-all duration-300 ${
              isCollapsed ? "px-2" : "px-4 lg:px-6"
            }`}
          >
            {menuGroups.map((group, groupIndex) => (
              <div key={groupIndex}>
                {groupIndex > 0 && !isCollapsed && (
                  <div className="mb-6 mt-2 border-t border-white/10"></div>
                )}
                {!isCollapsed && (
                  <h3 className="mb-5 ml-4 text-xs font-semibold uppercase tracking-wider text-bodydark2 dark:text-bodydark">
                    {group.name}
                  </h3>
                )}
                <ul
                  className={`flex flex-col gap-1 ${
                    !isCollapsed ? "mb-8" : "mb-4"
                  }`}
                >
                  {group.menuItems.map((menuItem, menuIndex) => (
                    <SidebarItem
                      key={menuIndex}
                      item={menuItem}
                      pageName={pageName}
                      setPageName={setPageName}
                      isCollapsed={isCollapsed}
                    />
                  ))}
                </ul>
              </div>
            ))}
          </nav>
          {/* <!-- Sidebar Menu --> */}
        </div>
      </aside>
    </ClickOutside>
  );
};

export default Sidebar;
